import { useState } from "react";
import useCurrency from "../hooks/useCurrency";

const InputBox = () => {
    const [selectedCurrency, setSelectedCurrency] = useState('usd');
    
    const currency = useCurrency('inr');
    const currencyOptions = Object.keys(currency);
    const [value, setValue] = useState(0);
    const [result,setResult] = useState(0);
    const convertedVal = (code) => {
        console.log('Code: ',code)
        console.log('Value: ', currencyOptions[code])
        const res = value* currencyOptions[code]
        setResult(res);
    }
    return(
        <div>
            <div>
                <h1>CurrencyConverter</h1>
            </div>
            <span><input placeholder="Enter Amount"
            value={value}
            onChange={(e) => setValue(Number(e.target.value))}
            ></input></span>
            <select
            onChange={(e) => convertedVal((e.target.value))}
            >
                {currencyOptions.map((item) => (
                <option>{item}</option>
                ))}
            </select>
            <div>
                <span>Result : </span>
                {result}
                <span></span>
                </div>
        </div>
    )
}
export default InputBox;
